            image: 'http://localhost:3000/image/iphone13banner.jpg',
            caption: ''
        },
        {
            image: 'http://localhost:3000/Image/samsungzfoldbanner.jpg',
            caption:''
        },
        {
            image: 'http://localhost:3000//Image/samzfold.jpg',
            caption:''
        }


    ];





    return(
        <Carousel data={data} time={8000} automatic={true}   dots={true} pauseIconColor="white" pauseIconSize="40px"  radius="10px" width="100vw" height="25em"
