import './reset.css';
import './App.css';
import './build/main.css'
import Main from '../src/organism/main'
import Footer from './organism/footer';
import Header from './organism/Header'
import { useEffect, useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import Itemdetail from './organism/itemDetail';
import Compare from './organism/comparision';
import Profilepage from './organism/profile';
import Cartdisplay from './organism/cartDisplay';



function App() {
  const [data, setData] = useState(null)
  const [compareValue,setCompare] =useState([])

  useEffect(() => {

    fetch('./data.json').then(
      function (res) {
        return res.json()
      }).then(function (data) {
        setData(data)
        console.log(data)
      }).catch(
        function (err) {
          console.log(err, "error")
        }
      )

  }, [Itemdetail])
  const [count, setCount] = useState([]);
  function increment() {
    setCount(prevCount => prevCount + 1)
  }
//localstorage
useEffect(() =>{

  const dataCart = JSON.parse(localStorage.getItem('dataCart'))

  if(dataCart) setCount(dataCart)

},[0])



useEffect(() =>{

  localStorage.setItem('dataCart', JSON.stringify(count))

},[count]) 


useEffect(() =>{

  const dataCompare = JSON.parse(localStorage.getItem('dataCompare'))

  if(dataCompare) setCompare(dataCompare)

},[0])



useEffect(() =>{

  localStorage.setItem('dataCompare', JSON.stringify(compareValue))

},[compareValue])








//close local
  console.log(data)
  const [showAlert, setShowAlert] = useState(false)
  const alertFunc = () => {

    setShowAlert(true)

  }
//search Event

const [eve,setEve] = useState('')
console.log("serachEve",eve)
console.log(compareValue)

//compareEvent
const [compareCard, SetCompareCard] = useState([])

  return (


    <Router>
      <div>

        <Header num={count} showAlert={showAlert} seacrhEvent={setEve} />
        <Switch>


          <Route path='/' exact component={() => data ? <Main count={count} incrementFunc={increment} mobileData={data} showAlert={showAlert} alertFunc={alertFunc} searchVal={eve} compareValue={compareValue} setCompare={setCompare} compareCard={compareCard} SetCompareCard={SetCompareCard} /> : ""} />
          <Route path='/shop/:id' component={() => data ? <Itemdetail incrementfunc={increment} mobiledata={data} />: ""} />

          <Route path='/profile' exact component={Profilepage} />
          <Route path='/compare' exact  component={() => data ? <Compare compareValue={compareValue} mobiles={data} />: ""} />
          <Route path='/cart'  exact  component={() => data ? <Cartdisplay compareValue={compareValue} mobiles={data} />: ""} />


          {/* {data ?
            <div>
              <Route path='/' exact component={() => <Main incrementFunc={increment} mobileData={data} />} />
              <Route path='/shop/:id' component={() => <Itemdetail incrementfunc={increment} mobiledata={data} />} />

            </div> : ""
          }
          <Route path='/profile'  exact component={Profilepage}/> */}

        </Switch>
        <Footer />

      </div>


      );
    </Router>
  )
}

export default App;
