import { Link } from "react-router-dom";
import Comparecard from "../../molecules/compareCard";
const Main = (props) => {
    const { mobileData, incrementFunc, alertFunc, showAlert, searchVal, compareValue, setCompare } = props;
    console.log(mobileData)

    return (
        // <h1>{mobileData}</h1>
        <main className="main">
             <Comparecard compareValue={compareValue} mobileData={mobileData} />
            <Link to="/compare">
             
                <button className="buyLink">comp</button>
            </Link>
            {mobileData.filter((val) => {
                if (searchVal === "") {
                    return val
                } else if (val.model.toLowerCase().includes(searchVal.toLowerCase()) || val.brand.toLowerCase().includes(searchVal.toLowerCase())) {
                    return val
                }
            }).map((data) => {

                return (
                    <div>


                        <div>

                            <Card incrementFunc={incrementFunc} brand={data.brand} model={data.model}
                                available={data.available}
                                actual={data.actual} price={data.price} image={data.image}
                                color={data.color} rating={data.rating}
                                bank={data.bank} emi={data.emi} id={data.id} slide={data.images} alertFunc={alertFunc} showAlert={showAlert} compareValue={compareValue} setCompare={setCompare} />
                        </div>


                        {// <div className="card">
                            //     <a href = "#">
