import './reset.css';
import './App.css';
import './build/main.css'
import Main from '../src/organism/main'
import Header from './organism/Header'
import { useEffect, useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import Itemdetail from './organism/itemDetail';
import Itemdisplay from './molecules/Itemdisplay';

function App() {
  const [data, setData] = useState(null)

  useEffect(() => {
    fetch('./data.json').then(
      function (res) {
        return res.json()
      }).then(function (data) {
        setData(data)
        console.log(data)
      }).catch(
        function (err) {
          console.log(err, "error")
        }
      )

  }, [])
  const [count, setCount] = useState(0);
  function increment() {
    setCount(prevCount => prevCount + 1)
  }

  console.log(data)


  return (


    <Router>
      <div>

        <Header num={count} />
        <Switch>

          {data ?
            <div>
              <Route path='/' exact component={() => <Main incrementFunc={increment} mobileData={data} />} />
              <Route path='/shop/:id' component={() => <Itemdetail mobiledata={data} />} />
            </div> : ""
          }


        </Switch>

      </div>


      );
    </Router>
  )
}

export default App;
