import './reset.css'
import './App.css';
import './build/main.css'
import Main from '../src/organism/main'
import Header from './organism/Header'
import { useEffect, useState } from 'react';
import {BrowserRouter as Router, Switch , Route} from 'react-router-dom'
import Card from './molecules/Card';
import itemDetail from './organism/itemDetail';

function App() {
  const [data, setData] = useState(null)

  useEffect(() => {
    fetch('./data.json').then(
      function (res) {
        return res.json()
      }).then(function (data) {
        setData(data)
      }).catch(
        function (err) {
          console.log(err, "error")
        }
      )

  }, [])
  const [count,setCount] = useState(0);
  function increment  (){
      setCount(prevCount => prevCount+1)
  }

  console.log(typeof data)
  return (
<Router>
    <div>

      <Header num={count} />
      <Switch>
      <Route path = '/' exact   component={()=> data ? <Main incrementFunc={increment} mobileData={data} /> :""}/>
       <Route path='/profile' component={ itemDetail} />
      </Switch>

    </div>


  );
  </Router>
  )
}

export default App;
