import Card from "../../molecules/Card";
// import Unavail from "../../molecules/unavail";
import { Link } from "react-router-dom";
import Comparecard from "../../molecules/compareCard";
import Banner from "../carousel";
const Main = (props) => {
    const { mobileData, incrementFunc, alertFunc, showAlert, searchVal, compareValue, setCompare ,count} = props;
    console.log(mobileData)

    return (
        // <h1>{mobileData}</h1>
        <div>
            <Banner />
            <main className="main">
                
                <Comparecard  setCompare={setCompare} compareValue={compareValue} mobileData={mobileData} />

                {mobileData.filter((val) => {
                    if (searchVal === "") {
                        return val
                    } else if (val.model.toLowerCase().includes(searchVal.toLowerCase()) || val.brand.toLowerCase().includes(searchVal.toLowerCase())) {
                        return val
                    }
                }).map((data) => {

                    return (
                        <div>


                            <div>

                                <Card incrementFunc={incrementFunc} brand={data.brand} model={data.model}
                                    available={data.available}
                                    actual={data.actual} price={data.price} image={data.image}
                                    color={data.color} rating={data.rating}
                                    bank={data.bank} emi={data.emi}  id={data.id} slide={data.images} count={count} alertFunc={alertFunc} showAlert={showAlert} compareValue={compareValue} setCompare={setCompare} />
                            </div>


                            {// <div className="card">
                                //     <a href = "#">
                                //         <img src={data.image} alt={data.model} />
