import Cart from "../cart";
import React, { useState } from "react";
import Button from "../../atoms/Button";
import { Link } from "react-router-dom";
var $ = require('jquery')

const Card = (props) => {
    const { brand, model,
        incrementFunc,
        available, actual,
        price, image,
        color, rating,
        bank, emi, id } = props;
    var calcDiscount = ((actual - price) / actual) * 100;
    var discount = calcDiscount.toFixed(2);
    var emiCalc = (price / 6).toFixed(2);
    //console.log(emiCalc)
    var buy, clickFunction;
    const alertFunc = ()=>{
        alert("Stock Un-Available")
        
    }

    if (available) {
        buy = "Add To Cart";
        clickFunction = incrementFunc;

    }
    else {
        buy = "Notify ME"
        clickFunction = alertFunc;
    };
        const buttonVal = React.createRef();
        const ref = ()=>{
            console.log(buttonVal.current.val)
        }
    return (


        <div className="card-wrap">
            <div className="card" >

                <a href="#">
                    <div >
                        <Link to={`shop/${id}`}>
                            <img src={image} alt={model} />
                            <div className="product-info">
                                <h4 className="brand">{brand} <span className="model">{model}</span></h4>

                                <p className=" price">MRP: <span>&#8377; </span > {price} <span className="actual"> &#8377; {actual}</span></p>
                                <p className="discount">  {discount} % Off </p>
                                <div className="division"></div>
                                <p onClick={ref}className="emi"><i role="presentation" airaHidden={true} className="fas fa-wallet"></i><a className="emilink" href="#">{emi} at &#8377;{emiCalc}/month </a></p>
                                <p>Offer {bank}</p>
                            </div>
                        </Link>
                    </div>
                </a>
                <div className="buy-link" id={model} onClick={clickFunction}  ref = {buttonVal}>
                    <Button classText="buyLink" value={id} buttonText={buy} />
                </div>



            </div>
        </div>
    )

}
export default Card
