import './reset.css';
import './App.css';
import './build/main.css'
import Main from '../src/organism/main'
import Footer from './organism/footer';
import Header from './organism/Header'
import { useEffect, useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import Itemdetail from './organism/itemDetail';

import Profilepage from './organism/profile';


function App() {
  const [data, setData] = useState(null)

  useEffect(() => {

    fetch('./data.json').then(
      function (res) {
        return res.json()
      }).then(function (data) {
        setData(data)
        console.log(data)
      }).catch(
        function (err) {
          console.log(err, "error")
        }
      )

  }, [Itemdetail])
  const [count, setCount] = useState(0);
  function increment() {
    setCount(prevCount => prevCount + 1)
  }

  console.log(data)
  const [showAlert, setShowAlert] = useState(false)
  const alertFunc = () => {

    setShowAlert(true)

  }



  return (


    <Router>
      <div>

        <Header num={count} showAlert={showAlert} />
        <Switch>


          <Route path='/' exact component={() => data ? <Main incrementFunc={increment} mobileData={data} showAlert={showAlert} alertFunc={alertFunc} /> : ""} />
          <Route path='/shop/:id' component={() => data ? <Itemdetail incrementfunc={increment} mobiledata={data} />: ""} />

          <Route path='/profile' exact component={Profilepage} />


          {/* {data ?
            <div>
              <Route path='/' exact component={() => <Main incrementFunc={increment} mobileData={data} />} />
