import { Link } from "react-router-dom"
import Card from "../Card"
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import { useState } from "react";
const Comparecard = (props) => {
    const { compareValue, mobileData } = props


    //carousel

    const responsive = {

        desktop: {
            breakpoint: { max: 3000, min: 1024 },
            items: 3
        },
        tablet: {
            breakpoint: { max: 1024, min: 464 },
            items: 2
        },
        mobile: {
            breakpoint: { max: 464, min: 0 },
            items: 1
        }
    };

    //endof carousel

    const [compareCard, setCompareCard] = useState(
        mobileData.filter((val) => {

            return compareValue.indexOf(val.model) > -1

        }).map((dat) => {
            return (
                <div className="compare-card-wrap">
                    <div className="compare-image">
                        <img src={dat.images[0]} alt={dat.model} />
                    </div>
                    <div className="price-brand-wrap">
                        <div className="brand-model">
                            <div className="brand">{dat.brand}</div>
                            <div className="wrap">{dat.model}</div>
                        </div>
                        <div className="price-info">
                            <div className="price">{dat.price}</div>

                        </div>
                    </div>
                </div>





            )
        }))

    return (
        <div className="compare-Card-detail">
            <div className="carousel-compare-card">
                <Carousel responsive={responsive}>
                    {compareCard}

                </Carousel>
            </div>
            <div className="compare-button">
                {
                    compareValue.length > 1 ? <div>
                        <Link to="/compare">

                            <button className="buyLink">Compare</button>
                        </Link>
                    </div> : ""
                }
            </div>
        </div>
    )

}
export default Comparecard;
