import { Link } from "react-router-dom"
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import { useEffect, useState } from "react";

const Compare = (props) => {
    const { compareValue, mobiles } = props
    console.log(" compareValue", compareValue)
    //carousel set up
    const responsive = {

        desktop: {
            breakpoint: { max: 3000, min: 1024 },
            items: 3
        },
        tablet: {
            breakpoint: { max: 1024, min: 464 },
            items: 2
        },
        mobile: {
            breakpoint: { max: 464, min: 0 },
            items: 1
        }
    };




    const d = mobiles.filter(val => {
        return compareValue.indexOf(val.model) > -1

    })
    console.log(d)
    const [mobData, setMobData] = useState(mobiles.filter((val) => {

        return compareValue.indexOf(val.model) > -1

    }))

    return (
        <div>


            <div className="compareInfo">
                <div className="title">
                    <div className="details-indepth">
                        <ul className="comparision">
                            <li className="detail-image text">Image</li>
                            <li>Brand</li>
                            <li>Model</li>
                            <li>Price</li>
                            <li>Discount</li>
                            <li>Offers</li>
                            <li>5g</li>
                            <li>Processor</li>
                            <li>Warrenty</li>
                            <li>Camera</li>
                            <li>Battery</li>
                            <li>Rating</li>
                            <li>Scrren_Size</li>
                            <li>Speakers</li>
                        </ul>



                    </div>
                    <Carousel responsive={responsive}>

                    {


                        mobData.map((dat) => {
                            return (

                                <div className="details-indepth">
                                    <ul className="comparision">
                                        <li className="detail-image"><img src={dat.image} ></img></li>
                                        <li>{dat.brand}</li>
                                        <li>{dat.model}</li>
                                        <li>Price</li>
                                        <li>Discount</li>
                                        <li>Offers</li>
                                        <li>5g</li>
                                        <li>Processor</li>
                                        <li>Warrenty</li>
                                        <li>Camera</li>
                                        <li>Battery</li>
                                        <li>Rating</li>
                                        <li>Scrren_Size</li>
                                        <li>Speakers</li>
                                    </ul>

                                </div>

                            )
                        })


                    }
                    </Carousel>



                </div>

            </div>


        </div>
    )
}
export default Compare
